bool iszs(int x){
    if(x == 0 || x == 1) return false;
    if(x == 2) return true;

    for(int i = 2; i < x;i++){
        if(x % i == 0) return false;
    }
    return true;
}

void dfs(int id,int sum,int order){
    if(id == k){
        if(iszs(sum)) ans++;
        return;
    }

    for(int i = order;i < n; i ++)
        dfs(id+1, sum + q[i],i + 1);
}

