    while(scanf("%lld%lld%lld",&a,&b,&c)){
        if(a == -1 && b == -1 && c == -1) break;
        int ans = w(a,b,c);
        printf("w(%lld, %lld, %lld) = %lld\n",a,b,c,ans);
    }
    return 0;
}

