using namespace std;

int n, a, b, m;             // m è¡¨ç¤º sum çš„æœ‰æ•ˆä½æœ€å¤§ä¸‹æ ‡
int p[1010], lp = 0;        // p å­˜å½“å‰æœ€å¤§å€¼ç»“æœï¼Œé«˜ç²¾åº¦æ•´æ•°ï¼Œlp æ˜¯æœ€å¤§ä¸‹æ ‡
int sum[1010];              // sum å­˜å½“å‰ä¹˜ç§¯ï¼Œé«˜ç²¾åº¦æ•´æ•°
int ans[1010], ls = 0;      // ans å­˜é™¤æ³•ç»“æœä¸´æ—¶æ•°ç»„ï¼Œls æ˜¯æœ€å¤§ä¸‹æ ‡
int res;                    // é™¤æ³•ä¸­ä¿å­˜çš„ä½™æ•°

struct node {
    int a, b;
    bool operator<(const node &W) const {
        return a * b < W.a * W.b;
    }
} arr[1010];

bool compare() {
    int i = 0, j = 0;
    while (i <= lp && p[i] == 0) i++;  // å»é™¤ p çš„å‰å¯¼ 0
    while (j <= ls && ans[j] == 0) j++;  // å»é™¤ ans çš„å‰å¯¼ 0

    int len1 = lp - i + 1;
    int len2 = ls - j + 1;
    if (len1 > len2) return false;
    if (len1 < len2) return true;

    while (i <= lp && j <= ls) {
        if (p[i] < ans[j]) return true;
        if (p[i] > ans[j]) return false;
        i++;
        j++;
    }
    return false;
}

void cheng(int d) {
    for (int i = 0; i <= m; i++) sum[i] *= arr[d].a;
    for (int i = 0; i <= m; i++) {
        sum[i + 1] += sum[i] / 10000;
        sum[i] %= 10000;
    }
    if (sum[m + 1] != 0) m++;
}

void div(int d) {
    memset(ans, 0, sizeof(ans));
    ls = 0;
    while (m >= 0 && sum[m] == 0) m--;  // å»æ‰å‰å¯¼0
    res = 0;
    int flag = 0;
    for (int i = m; i >= 0; i--) {
        res = res * 10000 + sum[i];
        ans[++ls] = res / arr[d].b;
        if (ans[ls] == 0 && !flag) ls--;  // ä¸ä¿ç•™å‰å¯¼ 0
        else flag = 1;
        res %= arr[d].b;
    }
}

int main() {
    cin >> n >> a >> b;
    for (int i = 0; i < n; i++) cin >> arr[i].a >> arr[i].b;
    sort(arr, arr + n);

    m = 0;
    memset(sum, 0, sizeof(sum));
    sum[0] = a;

    for (int i = 0; i < n; i++) {
        div(i);  // å…ˆé™¤
        if (compare()) {
            lp = ls;
            memcpy(p, ans, sizeof(ans));
        }
        cheng(i);  // å†ä¹˜
    }

    int i = 0;
    while (i <= lp && p[i] == 0) i++;  // å»å‰å¯¼0
    if (i > lp) {  // å…¨æ˜¯0
        printf("0\n");
        return 0;
    }

    printf("%d", p[i++]);
    for (; i <= lp; i++) {
        printf("%04d", p[i]);  // è¡¥è¶³4ä½
    }
    printf("\n");
    return 0;
}

