int dx[8] = {-2,-1,1,2, 2, 1,-1,-2};
int dy[8] = { 1, 2,2,1,-1,-2,-2,-1};
int n,m,x,y;

int main()
{
    cin >> n >> m >> x >> y;
    memset(f,-1,sizeof(f));
    memset(vis,false,sizeof(vis));

    f[x][y] = 0;
    vis[x][y] = true;
    q.push({x,y});

    while(!q.empty())
    {
        int xi = q.front().first,yi = q.front().second;
        q.pop();
        for(int i = 0; i < 8; i ++){
            int u = xi + dx[i], v = yi + dy[i];
            if(u < 1 || u > n || v < 1 || v > m || vis[u][v]) continue;
            vis[u][v] = true;
            q.push({u,v});
            f[u][v] = f[xi][yi] + 1;
        }
    }
    for(int i = 1; i <= n;i++){
        for(int j = 1; j <= m; j ++) cout << f[i][j] << " ";
        cout << endl;
    }

    return 0;
}

#include<bits/stdc++.h>

using namespace std;
int path[410][410];//è¡¨ç¤ºæœ€çŸ­è·ç¦»

int px[8] = {-2,-1,1,2, 2, 1,-1,-2};
int py[8] = { 1, 2,2,1,-1,-2,-2,-1};
int n,m,x,y;

bool iscan(int x,int y)
{
    if(x < 1 || x > n || y < 1 || y > m) return false;
    return true;
}

void dfs(int xi,int yi,int step)//è¡¨ç¤ºå½“å‰æ‰€åœ¨çš„ä½ç½®,ä»¥åŠåˆ°è¯¥ç‚¹ç”¨äº†å‡ æ­¥
{
    path[xi][yi] = step;
    for(int i = 0; i < 8;i++)
    {
        if(iscan(xi+px[i],yi+py[i]) && step + 1 < path[xi + px[i]][yi+py[i]]) 
            dfs(xi + px[i],yi+py[i],step + 1);
    }
    return ;
}

int main()
{
    memset(path,0x3f,sizeof(path));
    cin >> n >> m >> x >> y;
    dfs(x,y,0);
    for(int i = 1; i <= n; i ++)
    {
        for(int j = 1; j <= m;j++) 
        {
            if(path[i][j] == 0x3f3f3f3f) cout << -1 << " ";
            else cout << path[i][j] << " ";
        }
        cout << endl;
    }


    return 0;
}

#include<bits/stdc++.h>

using namespace std;
#define PII pair<int,int>
int n;
int a[310][310];//è®°å½•é™¨çŸ³ç ¸è½æ—¶é—´
int vis[310][310];//è®°å½•æ˜¯å¦èµ°è¿‡
int x,y,t;//é™¨çŸ³åæ ‡ï¼Œç ¸è½æ—¶é—´
int dist[310][310]; //è®°å½•åˆ°è¾¾æŸç‚¹çš„æœ€çŸ­æ—¶é—´

int dx[5]={0,0,0,1,-1};//æ–¹ä¾¿ç§»åŠ¨å’Œå¤„ç†é™¨çŸ³ç ¸è½
int dy[5]={0,1,-1,0,0};

int ch(int x){//åˆ¤æ–­è·¯è¿‡è¯¥ç‚¹æ—¶æ˜¯å¦é™¨çŸ³å·²ç»ç ¸è½ï¼Œå¦‚æœæ˜¯æ²¡æœ‰é™¨çŸ³ï¼Œç›¸å½“äºnå¹´åç ¸è½
    if (x==-1) return 99999;
    else return x;
}

void sign(int i,int t)//æ ‡è®°é™¨çŸ³çš„ä¸‹è½æ—¶é—´
{
    if(x + dx[i] >= 0 && y + dy[i] >= 0 && (a[x + dx[i]][y + dy[i]] == -1 || a[x + dx[i]][y + dy[i]] > t))
        a[x + dx[i]][y + dy[i]] = t;
}

int main()
{
    cin >> n;
    memset(a,-1,sizeof(a));//é™¨çŸ³ç ¸è½æ—¶é—´åˆå§‹åŒ–
    for(int i = 1; i <= n; i++){
        cin >> x >> y >> t;
        for(int i = 0; i < 5; i ++) sign(i,t);
    }

    queue<PII> q;
    vis[0][0] = true;
    q.push({0,0});
    while(!q.empty())
    {
        int x = q.front().first,y = q.front().second;
        q.pop();
        int s = dist[x][y] + 1;//ä¸‹ä¸€æ ¼å­åˆ°è¾¾çš„æ—¶é—´ç­‰äºå½“å‰æ ¼å­åŠ 1
        if(a[x][y] == -1){
            cout << s - 1;
            return 0;
        }
        for(int i = 1; i <= 4; i ++)
        {
            int xi = x + dx[i],yi = y + dy[i];
            if(xi >= 0 && yi >= 0 && s < ch(a[xi][yi]) && vis[xi][yi] == 0)//åœ¨è¾¹ç•Œå†…
            //ä¸”ä¸‹ä¸€æ—¶åˆ»é™¨çŸ³æ²¡ä¸‹æ¥¼ä¸”æ²¡è¢«è®¿é—®
            {
                q.push({xi,yi});
                vis[xi][yi] = 1;
                dist[xi][yi] = s;
            }
        }
    }
    cout << -1 << endl;
    return 0;
}

