#include<bits/stdc++.h>

using namespace std;
int a[30][30];//è¡¨ç¤ºåœ°çª–ä¹‹é—´çš„è¿æ¥æƒ…å†µ,1è¡¨ç¤ºæœ‰è¿æ¥
int path[30],ans;//å­˜æ”¾æœ€ç»ˆç»“æœçš„è·¯å¾„æ•°ç»„,æœ€ç»ˆçš„åœ°é›·æ•°
int tempath[30],temp;//ä¸­é—´å˜é‡
int used[30];//æ˜¯å¦ç”¨è¿‡
int passby;//è¡¨ç¤ºèµ°è¿‡çš„åœ°çª–æ•°
int pass_ans;//è¡¨ç¤ºæœ€ç»ˆèµ°è¿‡çš„åœ°çª–æ•°

int n;//è¡¨ç¤ºåœ°çª–æ•°
int num[30];//è¡¨ç¤ºåœ°çª–å†…çš„åœ°é›·æ•°ç»„

bool isconnect(int id)//åˆ¤æ–­æ˜¯å¦è¿˜æœ‰è·¯å¾„
{
    for(int i = 1;i <= n;i++)
        if(a[id][i] && !used[i]) return true;
    return false;
}

void dfs(int id)//å½“å‰å‡†å¤‡è¿›å…¥çš„åœ°çª–æ˜¯idå·åœ°çª–
{
    if(!isconnect(id)) //æ²¡è¿æ¥è¿”å›
    {
        if(temp > ans)//æ‰¾åˆ°äº†æ›´å¤šåœ°é›·çš„æ–¹æ¡ˆ
        {
            ans = temp;
            pass_ans = passby;
            for(int i = 1; i <= passby;i++) path[i] = tempath[i];
        }
        return ;
    }

    for(int i = 1; i <= n;i++)
    {
        if(a[id][i] && !used[i])
        {
            used[i] = 1;//è¯¥åœ°çª–èµ°è¿‡äº†
            passby++;//èµ°è¿‡çš„åœ°çª–æ•°â•â•
            tempath[passby] = i;
            temp += num[i];

            dfs(i);//å»è¯¥åœ°çª–

            used[i] = 0;
            tempath[passby] = 0;
            passby--;
            temp -= num[i];
        }
    }
}

int main()
{
    cin >> n;
    for(int i = 1; i <= n;i++) cin >> num[i];
    for(int i = 1; i <= n - 1;i++)//å…±n-1è¡Œ
    {
        int index = i + 1,t;
        for(int j = 1; j <= n - i;j++) {cin >> t; a[i][index] = t;
            index++;
        }   
    }

    for(int i = 1; i <= n;i++)//å› ä¸ºèµ·ç‚¹ä¸ä¸€å®šæ˜¯1,æ‰€ä»¥æ¯ä¸ªèµ·ç‚¹éƒ½éå†ä¸€ä¸‹
    {
        used[i] = 1;
        passby = 0;
        tempath[++passby] = i;
        temp += num[i];
        dfs(i);
        temp -= num[i];
        used[i] = 0;

    }
    for(int i = 1; i <= pass_ans;i++) cout << path[i] << " ";
    cout << endl << ans<< endl;
    return 0;
}
